CREATE TABLE users (id SERIAL PRIMARY KEY,name TEXT NOT NULL,email TEXT UNIQUE NOT NULL,password_hash TEXT NOT NULL,role TEXT NOT NULL CHECK (role IN ('admin','worker')),created_at TIMESTAMP DEFAULT NOW()); CREATE TABLE worker_logs (id SERIAL PRIMARY KEY,user_id INT NOT NULL REFERENCES users(id) ON DELETE CASCADE,clock_in TIMESTAMP,clock_out TIMESTAMP,break_minutes INT DEFAULT 0,gps_lat DOUBLE PRECISION,gps_lng DOUBLE PRECISION,address TEXT,job_name TEXT,created_at TIMESTAMP DEFAULT NOW()); 
